#include <utility.h>

using namespace std;

class Solution 
{
public:
	bool exist(vector<vector<char>>& board, string word) {
		if (board.empty() || board[0].empty() || word.empty())
			return false;
		int rowSize = board.size();
		int columnSize = board[0].size();
		vector<std::pair<int, int>> firstCharVec;
		for(int i = 0; i < rowSize; i ++)
			for (int j = 0; j < columnSize; j++)
			{
				if (board[i][j] == word[0])
					firstCharVec.push_back(std::make_pair(i, j));
			}

		vector<vector<bool>> map(rowSize, vector<bool>(columnSize, false));
		bool resOne = false;
		for (auto& a : firstCharVec)
		{
			map[a.first][a.second] = true;
			exitOne(board, word, a, 1, map, resOne);
			if(resOne)
				return true;
			map[a.first][a.second] = false;
		}

		return false;
	}

	void exitOne(vector<vector<char>>& board, string& word, std::pair<int,int> begin, int No, vector<vector<bool>>& map, bool & res)
	{
		if (No == word.size())
		{
			res = true; 
			return;
		}

		auto neighbours = neighbour(board.size(), board[0].size(),
			begin.first, begin.second);
		for(auto& neighbour : neighbours)
		{
			if (!map[neighbour.first][neighbour.second]
				&& word[No] == board[neighbour.first][neighbour.second])
			{
				map[neighbour.first][neighbour.second] = true;
				std::cout << "No : " << No << " " <<neighbour.first << ", " << neighbour.second << std::endl;
				exitOne(board, word, neighbour, No + 1, map, res);
				if(res)
					break;
				map[neighbour.first][neighbour.second] = false;
			}
		}
	}

	vector<std::pair<int, int>> neighbour(int rowSize, int columnSize, int i, int j)
	{
		vector<std::pair<int, int>> res;
		int p, q;
		
		//right
		p = i; q = j + 1;
		if ( q < columnSize)
			res.push_back(std::make_pair(p, q));

		//up
		p = i - 1; q = j;
		if (p >= 0 )
			res.push_back(std::make_pair(p, q));

		//left
		p = i; q = j - 1;
		if ( q >= 0 )
			res.push_back(std::make_pair(p, q));

		//down
		p = i + 1; q = j;
		if (p < rowSize )
			res.push_back(std::make_pair(p, q));
		
		return res;
	}
};

